<script>
    export let chapterNo;

    const handleClick = () => {
        window.state.goToMenu();

        // triger video reset
        setTimeout(() => { 
            let videoResetFn =  window['resetVideo' + chapterNo];
            typeof videoResetFn === 'function' && videoResetFn();
        }, window.state.transitionDuration);
    }
</script>

